#!/bin/bash

# This script prints numbers from 1 to 10
for i in {1..10}
do
  echo $i
done

#!/bin/bash

# Function to greet a user
greet() {
  echo "Hello, $1!"
}

greet "Alice"

#!/bin/bash

# Simple case statement
echo "Enter a number between 1 and 3:"
read NUM
case $NUM in
  1)
    echo "You entered one." ;;
  2)
    echo "You entered two." ;;
  3)
    echo "You entered three." ;;
  *)
    echo "Invalid input." ;;
esac
