void reverse(char s[]) {
	int c, i, j;
	for ( i = 0, j = strlen(s)-1; i < j; i++, j--) {
		c = s[i];
		s[i] = s[j];
		s[j] = c;
	}
}
void itoa(int n, char s[], int width) {
	int i, sign;
	if ((sign = n) < 0)
		n = -n;
	i = 0;
	do {
		s[i++] = n % 10 + '0';
		printf("%d %% %d + '0' = %d\n", n, 10, s[i-1]);
	} while ((n /= 10) > 0);
	if (sign < 0)
		s[i++] = '-';
	while (i < width)
		s[i++] = ' ';
	s[i] = '\0';
	reverse(s);
}

int hexalpha_to_int(int c)
{
	char hexalpha[] = "aAbBcCdDeEfF";
	int i;
	int answer = 0;
	for(i = 0; answer == 0 && hexalpha[i] != '\0'; i++)
	{
		if(hexalpha[i] == c)
		{
			answer = 10 + (i / 2);
		}
	}
	return answer;
}
unsigned int htoi(const char s[])
{
	unsigned int answer = 0;
	int i = 0;
	int valid = 1;
	int hexit;

	if(s[i] == '0')
	{
		++i;
		if(s[i] == 'x' || s[i] == 'X')
		{
			++i;
		}
	}
	while(valid && s[i] != '\0')
	{
		answer = answer * 16;
		if(s[i] >= '0' && s[i] <= '9')
		{
			answer = answer + (s[i] - '0');
		}
		else
		{
			hexit = hexalpha_to_int(s[i]);
			if(hexit == 0)
			{
				valid = 0;
			}
			else
			{
				answer = answer + hexit;
			}
		}
		++i;
	}
	if(!valid)
	{
		answer = 0;
	}
	return answer;
}

unsigned rightrot(unsigned x, unsigned n)
{
	while (n > 0) {
		if ((x & 1) == 1)
			x = (x >> 1) | ~(~0U >> 1);
		else
			x = (x >> 1);
		n--;
	}
	return x;
}
