public static int fibonacci(int n) {
	if (n <= 2) {
		return 1;
	} else {
		return fibonacci(n - 1) + fibonacci(n - 2);
}

public static int binarySearch(int[] arr, int x) {
	int low = 0;
	int mid = 0;
	int high = arr.length - 1;
	while (low <= high) {
		mid = (low + high) / 2;
		if (x == arr[mid]) {
			return mid;
		} else if (x > arr[mid]) {
			low = mid + 1;
		} else {
			high = mid - 1;
		}
	}
	return -1;
}

public static void insertionSort(int[] arr) {
	for (int i = 1; i < arr.length; i++) {
		int next = arr[i];
		int j;
		for (j = i - 1; j >= 0 && arr[j] > next; j--) {
			arr[j + 1] = arr[j];
		}
		arr[j + 1] = next;
	}
}

public static void selectionSort(int[] arr) {
	for (int last = arr.length - 1; last > 0; last--) {
		int largest = last;
		for (int i = 0; i < last; i++) {
			if (arr[i] > arr[largest]) {
				largest = i;
			}
		}
		int temp = arr[largest];
		arr[largest] = arr[last];
		arr[last] = temp;
	}
}

public static void bubbleSort(int[] arr) {
	for (int i = 1; i < arr.length; i++) {
		for (int j = 0; j < arr.length - i; j++) {
			if (arr[j] > arr[j + 1]) {
				int temp = arr[j];
				arr[j] = arr[j + 1];
				arr[j + 1] = temp;
			}
		}
	}
}

public static void mergeSort(int[] arr) {
	mergeSort(arr, 0, arr.length - 1);
}
public static void mergeSort(int[] arr, int leftmost, int rightmost) {
	if (leftmost < rightmost) {
		int mid = (leftmost + rightmost) / 2;
		mergeSort(arr, leftmost, mid);
		mergeSort(arr, mid + 1, rightmost);
		merge(arr, leftmost, mid, rightmost);
	}
}
public static void merge(int[] arr, int leftmost, int mid, int rightmost) {
	int[] subArray = new int[rightmost - leftmost + 1];
	int left = leftmost;
	int right = mid + 1;
	int i = 0;
	while (left <= mid && right <= rightmost) {
		if (arr[left] <= arr[right]) {
			subArray[i++] = arr[left++];
		} else {
			subArray[i++] = arr[right++];
		}
	}
	while (left <= mid) subArray[i++] = arr[left++];
	while (right <= rightmost) subArray[i++] = arr[right++];
	for (int j = 0; j < subArray.length; j++) {
		arr[leftmost + j] = subArray[j];
	}
}

public static void quickSort(int[] arr) {
	quickSort(arr, 0, arr.length - 1);
}
public static void quickSort(int[] arr, int i, int j) {
	if (i < j) {
		int pivotIdx = partition(arr, i, j);
		quickSort(arr, i, pivotIdx - 1);
		quickSort(arr, pivotIdx + 1, j);
	}
}
public static void swap(int[] arr, int i, int j) {
	int temp = arr[i];
	arr[i] = arr[j];
