public static int partition(int[] arr, int i, int j) {
	int pivot = arr[i];
	int m = i;
	for (int k = i + 1; k <= j; k++) {
		if (arr[k] < pivot) {
			m++;
			swap(arr, k, m);
		}
	}
	swap(arr, i, m);
	return m;
}

public static int quickSelect(int[] arr, int target) {
	return quickSelect(arr, target, 0, arr.length - 1);
}
public static int quickSelect(int[] arr, int target, int i, int j) {
	int pivotIdx = partition(arr, i, j);
	if (pivotIdx == target) {
		return arr[target];
	} else if (target < pivotIdx) {
		return quickSelect(arr, target, i, pivotIdx - 1);
	} else {
		return quickSelect(arr, target, pivotIdx + 1, j);
	}
}
