public int indexOf(int elem) {
	int index = 0;
	for (Node curr = head; curr != null; curr = curr.getTail()) {
		if (curr.getElem() == elem) {
			return index;
		} else {
			index++;
		}
	}
	return -1;
}

public void setTail(Node curr, Node next) {
	if (curr == null) {
		next.setNext(head);
		head = next;
	} else {
		next.setNext(curr.getNext());
		curr.setNext(next);
	}
	numNodes++;
}

public void addAtIndex(int index, int elem) {
