		System.out.println("index out of bounds");
	}
	Node curr = null;
	Node newNode = new Node(elem);
	if (index != 0) {
		curr = getNodeAtIndex(index - 1);
	}
	setTail(curr, newNode);
}

public int remove(Node curr) {
	int value;
	if (curr == null) {
		value = head.getElem();
		head = head.getNext();
	} else {
		value = curr.getNext().getElem();
		curr.setNext(curr.getNext().getNext());
	}
	numNodes--;
	return value;
}

public int removeAtIndex(int index) {
	if (index < 0 || index >= size()) {
		System.out.println("index out of bounds or list is empty");
	}
	Node curr = null;
	int elem = 0;
	if (index != 0) {
		curr = getNodeAtIndex(index - 1);
	}
	return remove(curr);
}

