public void enlargeArr() {
	int newSize = capacity * 2;
	int[] newArr = new int[newSize];
	for (int i = 0; i <= topIdx; i++) {
		newArr[i] = arr[i];
	}
	arr = newArr;
	capacity = newSize;
}

public Integer pop() {
	Integer elem = peek();
	if (elem != null) topIdx--;
	return elem;
}

public void push(Integer elem) {
	if (top >= capacity - 1) enlargeArr();
	topIdx++;
	arr[topIdx] = elem;
}

public Integer dequeue() {
	if (empty()) return null;
	Integer elem = arr[front];
	front = (front + 1) % capacity;
	return elem;
}

public void enqueue(Integer elem) {
	if (((back + 1) % capacity) == front) enlargeArr();
	arr[back] = elem;
	back = (back + 1) % capacity;
}

public static int[] bogoSort(int[] arr) {
	while (!isSorted(arr)) shuffle(arr);
	return arr;
}
public static void shuffle(int[] arr) {
	for (int i = 0; i < arr.length; i++) {
		swap(arr, i, (int) (Math.random() * i));
	}
}
