fun fibonacci(n: Int): Long {
	return if (n <= 1) {
		n.toLong()
	} else {
		var a = 0L
		var b = 1L
		var result = 0L

		for (i in 2..n) {
			result = a + b
			a = b
			b = result
		}

		result
		}
	}

fun <T : Comparable<T>> isSorted(arr: Array<T>): Boolean {
	for (i in 0 until arr.size - 1) {
		if (arr[i] > arr[i + 1]) {
			return false
		}
	}
	return true
}

fun <T> bogosort(arr: Array<T>) {
	while (!isSorted(arr)) {
		arr.shuffle()
	}
}

fun insertionSort(arr: IntArray) {
	val n = arr.size
	for (i in 1 until n) {
		val key = arr[i] 		var j = i - 1
		while (j >= 0 && arr[j] > key) {
			arr[j + 1] = arr[j]
			j--
		}
		arr[j + 1] = key
	}
}

