struct Tree<V> {
	children: Vec<Tree<V>>,
	value: V
}
impl<V> Tree<V> {
	fn bfs(&self, f: impl Fn(&V)) {
		let mut q = VecDeque::new();
		q.push_back(self);
		while let Some(t) = q.pop_front() {
			(f)(&t.value);
			for child in &t.children {
				q.push_back(child);
			}
		}
	}
}

