module fwrisc_alu (
	input clock,
	input reset,
	input[31:0] op_a,
	input[31:0] op_b,
	input[3:0] op,
	output reg[31:0] out);
	parameter [3:0]
		OP_ADD = 4'd0,
		OP_SUB = (OP_ADD+4'd1),
		OP_AND = (OP_SUB+4'd1),
		OP_OR = (OP_AND+4'd1),
		OP_CLR = (OP_OR+4'd1),
		OP_EQ = (OP_CLR+4'd1),
		OP_NE = (OP_EQ+4'd1),
		OP_LT = (OP_NE+4'd1),
		OP_GE = (OP_LT+4'd1),
		OP_LTU = (OP_GE+4'd1),
		OP_GEU = (OP_LTU+4'd1),
		OP_OPA = (OP_GEU+4'd1),
		OP_OPB = (OP_OPA+4'd1),
		OP_XOR = (OP_OPB+4'd1);
	always @* begin
		case (op)
			OP_ADD: out = op_a + op_b;
			OP_SUB: out = op_a - op_b;
			OP_AND: out = op_a & op_b;
			OP_OR: out = op_a | op_b;
			OP_CLR: out = op_b ^ (op_a & op_b);
			OP_EQ: out = {31'b0, op_a == op_b};
			OP_NE: out = {31'b0, op_a != op_b};
			OP_LT: out = {31'b0, $signed(op_a) < $signed(op_b)};
			OP_GE: out = {31'b0, $signed(op_a) >= $signed(op_b)};
			OP_LTU: out = {31'b0, op_a < op_b};
			OP_GEU: out = {31'b0, op_a >= op_b};
			OP_OPA: out = op_a;
			OP_OPB: out = op_b;
			default: out = op_a ^ op_b;
		endcase
	end
endmodule

module fwrisc_decode #(
	parameter ENABLE_COMPRESSED=1
	)(
	input clock,
	input reset,
	input fetch_valid,
	output decode_complete,
	input[31:0] instr_i,
	input instr_c,
	input[31:0] pc,
	output reg[5:0] ra_raddr,
	input[31:0] ra_rdata,
	output reg[5:0] rb_raddr,
	input[31:0] rb_rdata,
	output decode_valid,
	input exec_complete,
	output reg[31:0] op_a,
	output reg[31:0] op_b,
	output reg[31:0] op_c,
	output[3:0] op,
	output reg[5:0] rd_raddr,
	output reg[4:0] op_type);
	parameter [4:0]
		OP_TYPE_ARITH = 5'd0,
		OP_TYPE_BRANCH = (OP_TYPE_ARITH+5'd1),
		OP_TYPE_LDST = (OP_TYPE_BRANCH+5'd1),
		OP_TYPE_MDS = (OP_TYPE_LDST+5'd1),
		OP_TYPE_JUMP = (OP_TYPE_MDS+5'd1),
		OP_TYPE_SYSTEM = (OP_TYPE_JUMP+5'd1),
		OP_TYPE_CSR = (OP_TYPE_SYSTEM+5'd1);
	parameter [3:0]
		OP_ADD = 4'd0,
		OP_SUB = (OP_ADD+4'd1),
		OP_AND = (OP_SUB+4'd1),
		OP_OR = (OP_AND+4'd1),
		OP_CLR = (OP_OR+4'd1),
		OP_EQ = (OP_CLR+4'd1),
		OP_NE = (OP_EQ+4'd1),
		OP_LT = (OP_NE+4'd1),
		OP_GE = (OP_LT+4'd1),
		OP_LTU = (OP_GE+4'd1),
		OP_GEU = (OP_LTU+4'd1),
		OP_OPA = (OP_GEU+4'd1),
		OP_OPB = (OP_OPA+4'd1),
		OP_XOR = (OP_OPB+4'd1);
	parameter [3:0]
		OP_SLL = 4'd0,
		OP_SRL = (OP_SLL + 4'd1),
		OP_SRA = (OP_SRL + 4'd1),
		OP_MUL = (OP_SRA + 4'd1),
		OP_MULH = (OP_MUL + 4'd1),
		OP_MULS = (OP_MULH + 4'd1),
		OP_MULSH = (OP_MULS + 4'd1),
		OP_DIV = (OP_MULSH + 4'd1),
		OP_REM = (OP_DIV + 4'd1),
		OP_NUM_MDS = (OP_REM + 4'd1);
	parameter [3:0]
		OP_LB = 4'd0,
		OP_LH = (OP_LB+4'd1),
		OP_LW = (OP_LH+4'd1),
		OP_LBU = (OP_LW+4'd1),
		OP_LHU = (OP_LBU+4'd1),
		OP_SB = (OP_LHU + 4'd1),
		OP_SH = (OP_SB + 4'd1),
		OP_SW = (OP_SH + 4'd1),
		OP_NUM_MEM = (OP_SW + 4'd1);
	parameter [5:0]
		CSR_BASE_Q0 = 6'h20,
		CSR_MVENDORID = (CSR_BASE_Q0 + 1'd1),
		CSR_MARCHID = (CSR_MVENDORID + 1'd1),
		CSR_MIMPID = (CSR_MARCHID + 1'd1),
		CSR_MHARTID = (CSR_MIMPID + 1'd1),
		CSR_BASE_Q1 = 6'h28,
		CSR_MSTATUS = (CSR_BASE_Q1 + 1'd0),
		CSR_MISA = (CSR_MSTATUS + 1'd1),
		CSR_MEDELEG = (CSR_MISA + 1'd1),
		CSR_MIDELEG = (CSR_MEDELEG + 1'd1),
		CSR_MIE = (CSR_MIDELEG + 1'd1),
		CSR_MTVEC = (CSR_MIE + 1'd1),
		CSR_MCOUNTEREN = (CSR_MTVEC + 1'd1),
		CSR_BASE_Q2 = 6'h30,
		CSR_MSCRATCH = (CSR_BASE_Q2 + 1'd0),
		CSR_MEPC = (CSR_MSCRATCH + 1'd1),
		CSR_MCAUSE = (CSR_MEPC + 1'd1),
		CSR_MTVAL = (CSR_MCAUSE + 1'd1),
		CSR_MIP = (CSR_MTVAL + 1'd1),
		CSR_BASE_Q3 = 6'h38,
		CSR_MCYCLE = (CSR_BASE_Q3 + 1'd0),
		CSR_MCYCLEH = (CSR_MCYCLE + 1'd1),
		CSR_MINSTRET = (CSR_MCYCLEH + 1'd1),
		CSR_MINSTRETH = (CSR_MINSTRET + 1'd1),
		CSR_DEP_LO = (CSR_MINSTRETH + 1'd1),
		CSR_DEP_HI = (CSR_DEP_LO + 1'd1),
		CSR_SOFT_RESET = (CSR_DEP_HI + 1'd1);
	parameter [3:0]
		OP_TYPE_ECALL = 1'd0,
		OP_TYPE_EBREAK = (OP_TYPE_ECALL + 1'd1),
		OP_TYPE_ERET = (OP_TYPE_EBREAK + 1'd1);
	wire[31:0] instr;
	wire[31:0] imm_jump = $signed({instr[31], instr[19:12], instr[20], instr[30:21],1'b0});
	wire[31:0] auipc_imm_31_12 = {instr[31:12], {12{1'b0}}};
	wire[31:0] imm_11_0 = $signed({instr[31:20]});
	wire[31:0] st_imm_11_0 = $signed({instr[31:25], instr[11:7]});
	reg[31:0] imm_lui;
	wire[31:0] imm_branch = $signed({instr[31], instr[7], instr[30:25], instr[11:8], 1'b0});
	reg[4:0] op_type_w;
	reg[3:0] op_w;
	reg decode_valid_r;
	generate
		if (ENABLE_COMPRESSED) begin
			wire [31:0] instr_exp;
			fwrisc_c_decode u_c_decode (
				.clock    (clock), 
				.reset    (reset), 
				.instr_i  (instr_i[15:0]), 
				.instr    (instr_exp));
			assign instr = (instr_c)?instr_exp:instr_i;
		end else begin
			assign instr = instr_i;
		end
	endgenerate
	parameter[3:0]
		I_TYPE_R = 4'd0,
		I_TYPE_I = (I_TYPE_R+4'd1),
		I_TYPE_S = (I_TYPE_I+4'd1),
		I_TYPE_B = (I_TYPE_S+4'd1),
		I_TYPE_U = (I_TYPE_B+4'd1),
		I_TYPE_J = (I_TYPE_U+4'd1),
		I_TYPE_L = (I_TYPE_J+4'd1),
		I_TYPE_SY = (I_TYPE_L+4'd1);
	parameter[3:0]
		CI_TYPE_CR = 4'd0,
		CI_TYPE_CR_P = (CI_TYPE_CR+4'd1),
		CI_TYPE_CI = (CI_TYPE_CR_P+4'd1),
		CI_TYPE_CI_P = (CI_TYPE_CI+4'd1),
		CI_TYPE_CSS = (CI_TYPE_CI_P+4'd1),
		CI_TYPE_CIW = (CI_TYPE_CSS+4'd1),
		CI_TYPE_CL = (CI_TYPE_CIW+4'd1),
		CI_TYPE_CS = (CI_TYPE_CL+4'd1),
		CI_TYPE_CB = (CI_TYPE_CS+4'd1),
		CI_TYPE_CJ = (CI_TYPE_CB+4'd1);
	reg[2:0] i_type;
	wire c_rs_rd_eq_0 = (instr[11:7] == 0);
	wire c_rs_rd_eq_2 = (instr[11:7] == 2);
	wire c_rs2_eq_0 = (|instr[6:2] == 0);
	wire[5:0] c_rs1_rd_p = ({3'b001, instr[9:7]});
	wire[5:0] c_rd_rs2_p = ({3'b001, instr[4:2]});
	wire[5:0] c_rd_rs1 = instr[11:7];
	reg[5:0] rd_raddr_w;
	assign op = op_w;
	always @* begin
		op_w = 0;
		rd_raddr_w = instr[11:7];
		case (instr[6:4])
			3'b000: i_type = I_TYPE_L;
			3'b001: i_type = (instr[2])?I_TYPE_U:I_TYPE_I;
			3'b010: i_type = I_TYPE_S;
			3'b011: i_type = (instr[2])?I_TYPE_U:I_TYPE_R;
			3'b110: begin
				case (instr[3:2])
					2'b11: i_type = I_TYPE_J;
					2'b01: i_type = I_TYPE_I;
					default: i_type = I_TYPE_B; 
				endcase
			end
			3'b111: i_type = I_TYPE_SY;
			default: i_type = (instr[2])?I_TYPE_J:I_TYPE_B;
		endcase
		case (instr[6:4])
			3'b000: op_type_w=(&instr[3:2])?OP_TYPE_ARITH:OP_TYPE_LDST;
			3'b001: begin
				if (instr[2]) begin
					op_type_w = OP_TYPE_ARITH;
				end else if (instr[14:12] == 3'b101 || instr[14:12] == 3'b001) begin
					op_type_w = OP_TYPE_MDS;
				end else begin
					op_type_w = OP_TYPE_ARITH;
				end
			end
			3'b010: op_type_w = OP_TYPE_LDST;
			3'b011: begin
				if (instr[2]) begin
					op_type_w = OP_TYPE_ARITH;
				end else if (instr[14:12] == 3'b101 || instr[14:12] == 3'b001 || instr[25]) begin
					op_type_w = OP_TYPE_MDS;
				end else begin
					op_type_w = OP_TYPE_ARITH;
				end
			end
			3'b110: op_type_w = (instr[2])?OP_TYPE_JUMP:OP_TYPE_BRANCH;
			default: begin
				op_type_w = (|instr[14:12])?OP_TYPE_CSR:OP_TYPE_SYSTEM;
			end
		endcase
		case (op_type_w)
			OP_TYPE_SYSTEM: ra_raddr = CSR_MEPC;
			default: ra_raddr = instr[19:15];
		endcase
		case (op_type_w)
			OP_TYPE_CSR: begin
				case (instr[31:24])
					8'hF1: rb_raddr = (CSR_BASE_Q0 | instr[23:20]);
					8'h30: rb_raddr = (CSR_BASE_Q1 | instr[23:20]);
					8'h34: rb_raddr = (CSR_BASE_Q2 | instr[23:20]);
					8'hB0: rb_raddr = (instr[21])?CSR_MINSTRET:CSR_MCYCLE;
					8'hB8: begin
						rb_raddr = (instr[21])?CSR_MINSTRETH:CSR_MCYCLEH;
					end
					8'hBC: begin
						case (instr[21:20])
							2'b00: rb_raddr = CSR_DEP_LO;
							2'b01: rb_raddr = CSR_DEP_HI;
							default: rb_raddr = CSR_SOFT_RESET;
						endcase
					end
					default: rb_raddr = 0;
				endcase
			end
			default: rb_raddr = instr[24:20];
		endcase
		case (i_type) 
			I_TYPE_R, I_TYPE_I, I_TYPE_B, I_TYPE_S: begin
				op_a = ra_rdata;
			end
			I_TYPE_SY: begin
				if (instr[14]) begin
					op_a = instr[19:15];
				end else begin
					op_a = ra_rdata;
				end
			end
			I_TYPE_L: op_a = ra_rdata;
			I_TYPE_J: op_a = pc;
			I_TYPE_U: op_a = imm_lui;
			default: op_a = 0;
		endcase
		case (i_type)
			I_TYPE_R, I_TYPE_S, I_TYPE_SY, I_TYPE_B: begin
				op_b = rb_rdata;
			end
			I_TYPE_I: begin
				case (instr[14:12])
					3'b101, 3'b001: begin
						op_b = instr[24:20];
					end
					3'b011: begin
						op_b = $signed(instr[31:20]);
					end
					default: op_b = imm_11_0;
				endcase
			end
			I_TYPE_L: op_b = rb_rdata;
			I_TYPE_U: begin
				if (instr[5]) begin
					op_b = 32'b0;
				end else begin
					op_b = pc;
				end
			end
			default: op_b = 32'b0;
		endcase
		case (i_type)
			I_TYPE_B: op_c = imm_branch;
			I_TYPE_I,I_TYPE_L: op_c = imm_11_0;
			I_TYPE_S: op_c = st_imm_11_0;
			I_TYPE_J: op_c = imm_jump;
			I_TYPE_SY: begin
				op_c = rb_raddr;
			end
			default: op_c = 32'b0; 
		endcase
		case (op_type) 
			OP_TYPE_ARITH: begin
				if (instr[2]) begin
					op_w = (instr[5])?OP_OPA:OP_ADD;
				end else begin
					case (instr[14:12]) 
						3'b000: begin
							if (i_type == I_TYPE_R && instr[30]) begin
								op_w = OP_SUB;
							end else begin
								op_w = OP_ADD;
							end
						end
						3'b010: op_w = OP_LT;
						3'b011: op_w = OP_LTU;
						3'b100: op_w = OP_XOR;
						3'b110: op_w = OP_OR;
						default: op_w = OP_AND;
					endcase
				end
			end
			OP_TYPE_BRANCH: begin
				case (instr[14:12])
					3'b000: op_w = OP_EQ;
					3'b001: op_w = OP_NE;
					3'b100: op_w = OP_LT;
					3'b101: op_w = OP_GE;
					3'b110: op_w = OP_LTU;
					3'b111: op_w = OP_GEU;
					default: op_w = OP_GEU;
				endcase
			end
			OP_TYPE_LDST: begin
				case ({instr[5], instr[14:12]})
					4'b0000: op_w = OP_LB;
					4'b0001: op_w = OP_LH;
					4'b0010: op_w = OP_LW;
					4'b0100: op_w = OP_LBU;
					4'b0101: op_w = OP_LHU;
					4'b1000: op_w = OP_SB;
					4'b1001: op_w = OP_SH;
					default: op_w = OP_SW;
				endcase
			end
			OP_TYPE_MDS: begin
				case (instr[14:12])
					3'b001: op_w = OP_SLL;
					default: op_w = (instr[30])?OP_SRA:OP_SRL;
				endcase
			end
			OP_TYPE_CSR: begin
				case (instr[13:12])
					2'b01: op_w = OP_OPA;
					2'b10: op_w = OP_OR;
					default: op_w = OP_CLR;
				endcase
			end
			OP_TYPE_SYSTEM: begin
				case (instr[31:28])
					4'b0000: op_w = (instr[20])?OP_TYPE_EBREAK:OP_TYPE_ECALL;
					default: op_w = OP_TYPE_ERET;
				endcase
			end
			default: op_w = 0;
		endcase
	end
	parameter [1:0]
		STATE_DECODE = 2'd1,
		STATE_REG = (STATE_DECODE + 2'd1);
	reg [1:0] decode_state;
	assign decode_complete = exec_complete;
	assign decode_valid = (decode_valid_r && !exec_complete);
	always @(posedge clock) begin
		if (reset) begin
			decode_state <= STATE_DECODE;
			decode_valid_r <= 1'b0;
			rd_raddr <= 0;
			imm_lui <= 0;
			op_type <= 0;
		end else begin
			case (decode_state) 
				STATE_DECODE: begin
					if (fetch_valid) begin
						decode_state <= STATE_REG;
						imm_lui <= {instr[31:12], 12'h000};
						rd_raddr <= rd_raddr_w;
						op_type <= op_type_w;
						decode_valid_r <= 1'b1;
					end else begin
						decode_valid_r <= 1'b0;
					end
				end
				default: begin
					if (exec_complete) begin
						decode_state <= STATE_DECODE;
						decode_valid_r <= 1'b0;
					end
				end
			endcase
		end
	end
endmodule

module fwrisc_exec #(
	parameter ENABLE_COMPRESSED=1,
	parameter ENABLE_MUL_DIV=1,
	parameter ENABLE_DEP=1
	)(
	input clock,
	input reset,
	input decode_valid,
	output reg instr_complete,
	output reg trap,
	output reg tret,
	input instr_c,
	input[4:0] op_type,
	input[31:0] op_a,
	input[31:0] op_b,
	input[3:0] op,
	input[31:0] op_c,
	input[5:0] rd,
	output reg[5:0] rd_waddr,
	output reg[31:0] rd_wdata,
	output reg rd_wen,
	output reg[31:0] pc,
	output reg pc_seq,
	input[31:0] mtvec,
	input[31:0] dep_lo,
	input[31:0] dep_hi,
	output[31:0] daddr,
	output dvalid,
	output dwrite,
	output[31:0] dwdata,
	output[3:0] dwstb,
	input[31:0] drdata,
	input dready,
	input irq,
	input meie,
	input mie);
	parameter [3:0]
		OP_ADD = 4'd0,
		OP_SUB = (OP_ADD+4'd1),
		OP_AND = (OP_SUB+4'd1),
		OP_OR = (OP_AND+4'd1),
		OP_CLR = (OP_OR+4'd1),
		OP_EQ = (OP_CLR+4'd1),
		OP_NE = (OP_EQ+4'd1),
		OP_LT = (OP_NE+4'd1),
		OP_GE = (OP_LT+4'd1),
		OP_LTU = (OP_GE+4'd1),
		OP_GEU = (OP_LTU+4'd1),
		OP_OPA = (OP_GEU+4'd1),
		OP_OPB = (OP_OPA+4'd1),
		OP_XOR = (OP_OPB+4'd1);
	parameter [3:0]
		OP_LB = 4'd0,
		OP_LH = (OP_LB+4'd1),
		OP_LW = (OP_LH+4'd1),
		OP_LBU = (OP_LW+4'd1),
		OP_LHU = (OP_LBU+4'd1),
		OP_SB = (OP_LHU + 4'd1),
		OP_SH = (OP_SB + 4'd1),
		OP_SW = (OP_SH + 4'd1),
		OP_NUM_MEM = (OP_SW + 4'd1);
	parameter [4:0]
		OP_TYPE_ARITH = 5'd0,
		OP_TYPE_BRANCH = (OP_TYPE_ARITH+5'd1),
		OP_TYPE_LDST = (OP_TYPE_BRANCH+5'd1),
		OP_TYPE_MDS = (OP_TYPE_LDST+5'd1),
		OP_TYPE_JUMP = (OP_TYPE_MDS+5'd1),
		OP_TYPE_SYSTEM = (OP_TYPE_JUMP+5'd1),
		OP_TYPE_CSR = (OP_TYPE_SYSTEM+5'd1);
	parameter [5:0]
		CSR_BASE_Q0 = 6'h20,
		CSR_MVENDORID = (CSR_BASE_Q0 + 1'd1),
		CSR_MARCHID = (CSR_MVENDORID + 1'd1),
		CSR_MIMPID = (CSR_MARCHID + 1'd1),
		CSR_MHARTID = (CSR_MIMPID + 1'd1),
		CSR_BASE_Q1 = 6'h28,
		CSR_MSTATUS = (CSR_BASE_Q1 + 1'd0),
		CSR_MISA = (CSR_MSTATUS + 1'd1),
		CSR_MEDELEG = (CSR_MISA + 1'd1),
		CSR_MIDELEG = (CSR_MEDELEG + 1'd1),
		CSR_MIE = (CSR_MIDELEG + 1'd1),
		CSR_MTVEC = (CSR_MIE + 1'd1),
		CSR_MCOUNTEREN = (CSR_MTVEC + 1'd1),
		CSR_BASE_Q2 = 6'h30,
		CSR_MSCRATCH = (CSR_BASE_Q2 + 1'd0),
		CSR_MEPC = (CSR_MSCRATCH + 1'd1),
		CSR_MCAUSE = (CSR_MEPC + 1'd1),
		CSR_MTVAL = (CSR_MCAUSE + 1'd1),
		CSR_MIP = (CSR_MTVAL + 1'd1),
		CSR_BASE_Q3 = 6'h38,
		CSR_MCYCLE = (CSR_BASE_Q3 + 1'd0),
		CSR_MCYCLEH = (CSR_MCYCLE + 1'd1),
		CSR_MINSTRET = (CSR_MCYCLEH + 1'd1),
		CSR_MINSTRETH = (CSR_MINSTRET + 1'd1),
		CSR_DEP_LO = (CSR_MINSTRETH + 1'd1),
		CSR_DEP_HI = (CSR_DEP_LO + 1'd1),
		CSR_SOFT_RESET = (CSR_DEP_HI + 1'd1);
	parameter [3:0]
		OP_TYPE_ECALL = 1'd0,
		OP_TYPE_EBREAK = (OP_TYPE_ECALL + 1'd1),
		OP_TYPE_ERET = (OP_TYPE_EBREAK + 1'd1);
	parameter [3:0]
		STATE_EXECUTE = 4'd0,
		STATE_BRANCH_TAKEN = (STATE_EXECUTE + 4'd1),
		STATE_JUMP = (STATE_BRANCH_TAKEN + 4'd1),
		STATE_CSR = (STATE_JUMP + 4'd1),
		STATE_MDS_COMPLETE = (STATE_CSR + 4'd1),
		STATE_LDST_COMPLETE = (STATE_MDS_COMPLETE + 4'd1),
		STATE_EXCEPTION_1 = (STATE_LDST_COMPLETE + 4'd1),
		STATE_EXCEPTION_2 = (STATE_EXCEPTION_1 + 4'd1),
		STATE_EXCEPTION_3 = (STATE_EXCEPTION_2 + 4'd1);
	reg [3:0] exec_state;
	reg[31:0] pc_next;
	reg pc_seq_next;
	wire mds_in_valid = (
		(op_type == OP_TYPE_MDS && exec_state == STATE_EXECUTE)
		&& decode_valid
	);
	wire mds_out_valid;
	wire[31:0] mds_out;
	wire mem_req_valid;
	wire[31:0] mem_req_addr;
	wire mem_ack_valid;
	wire[31:0] mem_ack_data;
	reg[2:0] next_pc_seq_incr;
	reg mcause_int;
	reg[3:0] mcause;
	wire[31:0] next_pc_seq = pc + next_pc_seq_incr;
	reg[31:0] mtval = 0;
	wire [31:0] alu_out;
	wire dep_violation;
	generate
	if (ENABLE_DEP) begin
		assign dep_violation = (
			dep_lo[0] && dep_hi[0]
			&& (exec_state == STATE_JUMP) 
			&& !(alu_out[31:3] >= dep_lo[31:3] && alu_out[31:3] <= dep_hi[31:3])
		);
	end else begin
		assign dep_violation = 0;
	end
	endgenerate
	wire ei_req = (irq && meie && mie);
	wire jump_target_misaligned;
	generate
	if (!ENABLE_COMPRESSED) begin
		assign jump_target_misaligned = (
			(exec_state == STATE_JUMP || exec_state == STATE_BRANCH_TAKEN) 
			&& alu_out[1]
		);
	end else begin
		assign jump_target_misaligned = 0;
	end
	endgenerate
	reg ldst_addr_misaligned;
	always @* begin
		if (exec_state == STATE_EXECUTE && op_type == OP_TYPE_LDST) begin
			case (op)
				OP_LH, OP_LHU, OP_SH: ldst_addr_misaligned = alu_out[0];
				OP_LW, OP_SW: ldst_addr_misaligned = |alu_out[1:0];
				default: ldst_addr_misaligned = 0;
			endcase
		end else begin
			ldst_addr_misaligned = 0;
		end
	end
	always @(posedge clock) begin
		if (reset) begin
			mtval <= 0;
		end else begin
			if (jump_target_misaligned || dep_violation || ldst_addr_misaligned) begin
				case (op_type)
					OP_TYPE_JUMP: mtval <= {alu_out[31:1], 1'b0};
					default: mtval <= alu_out;
				endcase
			end
		end
	end
	always @* begin
		if (exec_state == STATE_BRANCH_TAKEN || exec_state == STATE_JUMP) begin
			next_pc_seq_incr = 0;
		end else begin
			next_pc_seq_incr = (instr_c)?2:4;
		end
	end
	wire branch_taken = ((op_type == OP_TYPE_BRANCH && alu_out[0]) || op_type == OP_TYPE_JUMP);
	always @* begin
		if (exec_state == STATE_BRANCH_TAKEN || exec_state == STATE_JUMP) begin
			pc_next = {alu_out[31:1], 1'b0};
			pc_seq_next = 0;
		end else begin
			pc_next = next_pc_seq;
			pc_seq_next = 1;
		end
	end
	always @(posedge clock) begin
		if (reset) begin
			exec_state <= STATE_EXECUTE;
			instr_complete <= 1'b0;
			trap <= 1'b0;
			tret <= 1'b0;
			pc <= 'h8000_0000;
			pc_seq <= 1;
			mcause <= 4'b0;
			mcause_int <= 1'b0;
		end else begin
			case (exec_state)
				STATE_EXECUTE: begin
					if (decode_valid) begin
						if (ei_req) begin
							exec_state <= STATE_EXCEPTION_1;
							mcause <= 4'd11;
							mcause_int <= 1'b1;
						end else begin
							case (op_type)
								OP_TYPE_ARITH: begin
									pc <= pc_next;
									pc_seq <= pc_seq_next;
									instr_complete <= 1;
								end
								OP_TYPE_BRANCH: begin
									if (alu_out[0]) begin
										exec_state <= STATE_BRANCH_TAKEN;
									end else begin
										pc <= pc_next;
										pc_seq <= pc_seq_next;
										instr_complete <= 1;
									end
								end
								OP_TYPE_LDST: begin
									if (ldst_addr_misaligned) begin
										if (op == OP_SB || op == OP_SH || op == OP_SW) begin
											mcause <= 6; 
											mcause_int <= 1'b0;
										end else begin
											mcause <= 4; 
											mcause_int <= 1'b0;
										end
										exec_state <= STATE_EXCEPTION_1;
									end else begin
										exec_state <= STATE_LDST_COMPLETE;
									end
								end
								OP_TYPE_MDS: begin
									exec_state <= STATE_MDS_COMPLETE;
								end
								OP_TYPE_JUMP: begin
									exec_state <= STATE_JUMP;
								end
								OP_TYPE_SYSTEM: begin
									if (op == OP_TYPE_ERET) begin
										instr_complete <= 1'b1;
										tret <= 1'b1;
										pc <= op_a; 
										pc_seq <= 0;
										exec_state <= STATE_EXECUTE;
									end else begin
										mcause <= (op == OP_TYPE_EBREAK)?3:11;
										mcause_int <= 1'b0;
										exec_state <= STATE_EXCEPTION_1;
									end
								end
								default: begin
									exec_state <= STATE_CSR;
								end
							endcase
						end
					end else begin
						instr_complete <= 1'b0;
						trap <= 1'b0;
						tret <= 1'b0;
					end
				end
				STATE_CSR: begin
					pc <= pc_next;
					pc_seq <= pc_seq_next;
					exec_state <= STATE_EXECUTE;
					instr_complete <= 1;
				end
				STATE_JUMP: begin
					case ({dep_violation, jump_target_misaligned})
						2'b00: begin
							pc <= {alu_out[31:1], 1'b0};
							pc_seq <= 0;
							exec_state <= STATE_EXECUTE;
							instr_complete <= 1;
						end
						2'b01: begin
							mcause <= 0;
							mcause_int <= 1'b0;
							exec_state <= STATE_EXCEPTION_1;
						end
						default: begin
							mcause <= 1;
							mcause_int <= 1'b0;
							exec_state <= STATE_EXCEPTION_1;
						end
					endcase
				end
				STATE_BRANCH_TAKEN: begin
					if (jump_target_misaligned) begin
						mcause <= 0;
						mcause_int <= 1'b0;
						exec_state <= STATE_EXCEPTION_1;
					end else begin
						pc <= alu_out;
						pc_seq <= pc_seq_next;
						exec_state <= STATE_EXECUTE;
						instr_complete <= 1;
					end
				end
				STATE_MDS_COMPLETE: begin
					if (mds_out_valid) begin
						exec_state <= STATE_EXECUTE;
						pc <= pc_next;
						pc_seq <= pc_seq_next;
						instr_complete <= 1;
					end
				end
				STATE_LDST_COMPLETE: begin
					if (mem_ack_valid) begin
						exec_state <= STATE_EXECUTE;
						pc <= pc_next;
						pc_seq <= pc_seq_next;
						instr_complete <= 1;
					end
				end
				STATE_EXCEPTION_1: begin
					exec_state <= STATE_EXCEPTION_2;
				end
				STATE_EXCEPTION_2: begin
					exec_state <= STATE_EXCEPTION_3;
				end
				STATE_EXCEPTION_3: begin
					pc <= mtvec;
					pc_seq <= 1'b0;
					instr_complete <= 1'b1;
					trap <= 1'b1;
					exec_state <= STATE_EXECUTE;
				end
			endcase
		end
	end
	wire alu_op_a_sel_pc = (
		(exec_state == STATE_BRANCH_TAKEN)
		|| (exec_state == STATE_EXECUTE && op_type == OP_TYPE_JUMP)
	);
	wire alu_op_b_sel_c = (
		(exec_state == STATE_BRANCH_TAKEN)
		|| (exec_state == STATE_JUMP)
		|| (exec_state == STATE_EXECUTE && op_type == OP_TYPE_LDST)
	);
	wire alu_op_sel_add = (
		(exec_state == STATE_EXECUTE && op_type == OP_TYPE_LDST)
		|| (exec_state == STATE_BRANCH_TAKEN)
		|| (exec_state == STATE_JUMP)
	);
	wire alu_op_sel_opb = (
		(exec_state == STATE_CSR)
	);
	wire alu_op_sel_opa = (
		(exec_state == STATE_EXECUTE && op_type == OP_TYPE_JUMP)
		|| (exec_state == STATE_JUMP)
	);
	wire [31:0] alu_op_a = (alu_op_a_sel_pc)?next_pc_seq:op_a;
	wire [31:0] alu_op_b = (alu_op_b_sel_c)?op_c:op_b;
	reg [3:0] alu_op;
	always @* begin
		case ({alu_op_sel_add,alu_op_sel_opb,alu_op_sel_opa})
			3'b100: alu_op = OP_ADD;
			3'b010: alu_op = OP_OPB;
			3'b001: alu_op = OP_OPA;
			default: alu_op = op;
		endcase
	end
	always @* begin
		rd_wen = (decode_valid && !instr_complete &&
				(
					(exec_state == STATE_EXECUTE) && 
						(op_type == OP_TYPE_ARITH || op_type == OP_TYPE_JUMP 
							|| op_type == OP_TYPE_CSR)
					|| (exec_state == STATE_CSR)
					|| (exec_state == STATE_LDST_COMPLETE 
						&& (op == OP_LB || op == OP_LH || op == OP_LW
							|| op == OP_LBU || op == OP_LHU) && mem_ack_valid)
					|| (exec_state == STATE_MDS_COMPLETE && mds_out_valid)
					|| (exec_state == STATE_EXCEPTION_1 || exec_state == STATE_EXCEPTION_2 || exec_state == STATE_EXCEPTION_3)
				)
		);
	end
	always @* begin
		case (exec_state)
			STATE_EXCEPTION_1: rd_wdata = pc;
			STATE_EXCEPTION_2: rd_wdata = mtval;
			STATE_EXCEPTION_3: rd_wdata = {mcause_int, 27'b0, mcause};
			STATE_MDS_COMPLETE: rd_wdata = mds_out;
			STATE_LDST_COMPLETE: rd_wdata = mem_ack_data;
			default: rd_wdata = alu_out;
		endcase
		case (exec_state)
			STATE_EXECUTE: rd_waddr = (op_type == OP_TYPE_CSR)?op_c[5:0]:rd;
			STATE_EXCEPTION_1: rd_waddr = CSR_MEPC;
			STATE_EXCEPTION_2: rd_waddr = CSR_MTVAL;
			STATE_EXCEPTION_3: rd_waddr = CSR_MCAUSE;
			default: rd_waddr = rd;
		endcase
	end
	fwrisc_alu u_alu (
		.clock (clock), 
		.reset (reset), 
		.op_a (alu_op_a), 
		.op_b (alu_op_b), 
		.op (alu_op), 
		.out (alu_out));
	fwrisc_mul_div_shift #(
		.ENABLE_MUL_DIV (ENABLE_MUL_DIV)
		) u_mds (
		.clock (clock), 
		.reset (reset), 
		.in_a (op_a), 
		.in_b (op_b), 
		.op (op[3:0]), 
		.in_valid (mds_in_valid), 
		.out (mds_out), 
		.out_valid (mds_out_valid));
	assign mem_req_addr = alu_out;
	assign mem_req_valid = (
		exec_state == STATE_EXECUTE 
		&& op_type == OP_TYPE_LDST
		&& decode_valid && !ldst_addr_misaligned);
	fwrisc_mem u_mem (
		.clock (clock), 
		.reset (reset), 
		.req_valid (mem_req_valid), 
		.req_addr (mem_req_addr), 
		.req_op (op[3:0]), 
		.req_data (op_b), 
		.ack_valid (mem_ack_valid), 
		.ack_data (mem_ack_data), 
		.dvalid (dvalid), 
		.daddr (daddr), 
		.dwdata (dwdata), 
		.dwstb (dwstb), 
		.dwrite (dwrite), 
		.drdata (drdata), 
		.dready (dready));
endmodule

module fwrisc_fetch #(
	parameter ENABLE_COMPRESSED=1
	)(
	input clock,
	input reset,
	input[31:0] next_pc,
	input next_pc_seq,
	output reg[31:0] iaddr,
	input[31:0] idata,
	output ivalid,
	input iready,
	output fetch_valid,
	input decode_complete,
	output reg[31:0] instr,
	output reg instr_c);
	reg[2:0] state;
	reg[15:0] instr_cache;
	wire instr_cache_c = (&instr_cache[1:0] != 1);
	reg instr_cache_valid;
	wire instr_c_lo = (&idata[1:0] != 1);
	wire instr_c_hi = (&idata[17:16] != 1);
	wire instr_c_next = (next_pc[1])?instr_c_hi:instr_c_lo;
	parameter [2:0]
		STATE_FETCH1 = 3'd0,
		STATE_FETCH2 = (STATE_FETCH1 + 3'd1),
		STATE_WAIT_DECODE = (STATE_FETCH2 + 3'd1);
	reg fetch_valid_r;
	assign fetch_valid = (fetch_valid_r && !decode_complete);
	reg ivalid_r;
	assign ivalid = (ivalid_r);
	always @(posedge clock) begin
		if (reset) begin
			state <= STATE_FETCH1;
			instr_cache_valid <= 0;
			instr_cache <= {16{1'b0}};
			fetch_valid_r <= 0;
			ivalid_r <= 0;
			instr_c <= 0;
			instr <= {32{1'b0}};
		end else begin
			case (state)
				default: begin
					iaddr <= {next_pc[31:2], 2'b0};
					if (iready && ivalid_r) begin
						instr_c <= instr_c_next;
						case ({next_pc[1], instr_c_next})
							2'b00: begin
								instr <= idata;
								instr_cache_valid <= 0;
								fetch_valid_r <= 1;
								ivalid_r <= 0;
								state <= STATE_WAIT_DECODE;
							end
							2'b01: begin
								instr <= idata[15:0];
								instr_cache <= idata[31:16];
								instr_cache_valid <= 1;
								fetch_valid_r <= 1;
								ivalid_r <= 0;
								state <= STATE_WAIT_DECODE;
							end
							2'b10: begin
								instr[15:0] <= idata[31:16];
								instr_cache_valid <= 0;
								ivalid_r <= 1;
								state <= STATE_FETCH2;
							end
							2'b11: begin
								instr[15:0] <= idata[31:16];
								instr_cache_valid <= 0;
								ivalid_r <= 0;
								state <= STATE_WAIT_DECODE; 
							end
						endcase
					end else begin
						ivalid_r <= 1;
					end
				end   
				STATE_WAIT_DECODE: begin
					if (decode_complete) begin
						fetch_valid_r <= 0;
						state <= STATE_FETCH1;
`ifdef UNDEFINED
						if (!next_pc_seq) begin
							instr_cache_valid <= 0;
							state <= STATE_FETCH1;
						end else begin
							case ({instr_c, instr_cache_valid, instr_cache_c})
								3'b111: begin 
									instr_cache_valid <= 0;
									instr <= instr_cache;
									instr_c <= 1;
									state <= 3'b001; 
								end
								default: begin
									instr_cache_valid <= 0;
									state <= STATE_FETCH1;
								end
							endcase
						end
`endif
					end
				end
				STATE_FETCH2: begin
					iaddr <= {next_pc[31:2]+1'd1, 2'b0};
					if (iready) begin
						ivalid_r <= 0;
						instr[31:16] <= idata[15:0];
						instr_cache <= idata[31:16];
						instr_cache_valid <= 1;
						fetch_valid_r <= 1;
						state <= STATE_WAIT_DECODE;
					end
				end
			endcase
		end
	end
endmodule

module fwrisc_mem (
	input clock,
	input reset,
	input req_valid,
	input[31:0] req_addr,
	input[3:0] req_op,
	input[31:0] req_data,
	output reg ack_valid,
	output reg[31:0] ack_data,
	output reg dvalid,
	output reg[31:0] daddr,
	output reg[31:0] dwdata,
	output reg[3:0] dwstb,
	output reg dwrite,
	input[31:0] drdata,
	input dready);
	parameter [3:0]
		OP_LB = 4'd0,
		OP_LH = (OP_LB+4'd1),
		OP_LW = (OP_LH+4'd1),
		OP_LBU = (OP_LW+4'd1),
		OP_LHU = (OP_LBU+4'd1),
		OP_SB = (OP_LHU + 4'd1),
		OP_SH = (OP_SB + 4'd1),
		OP_SW = (OP_SH + 4'd1),
		OP_NUM_MEM = (OP_SW + 4'd1);
	parameter[1:0]
		STATE_WAIT_REQ = 2'd0,
		STATE_WAIT_RSP = (STATE_WAIT_REQ + 2'd1);
	reg[1:0] mem_state;
	always @(posedge clock) begin
		if (reset) begin
			ack_valid <= 0;
			ack_data <= {32{1'b0}};
			mem_state <= 0;
			dvalid <= 0;
			daddr <= 0;
			dwdata <= 0;
			dwstb <= 0;
			dwrite <= 0;
		end else begin
			case (mem_state)
				STATE_WAIT_REQ: begin
					ack_valid <= 0;
					if (req_valid && !ack_valid) begin
						dvalid <= 1;
						daddr <= req_addr;
						mem_state <= STATE_WAIT_RSP;
						dwrite <= (req_op == OP_SB || req_op == OP_SH || req_op == OP_SW);
						case (req_op)
							OP_SB: begin
								case (req_addr[1:0])
									2'b00: dwstb <= 4'b0001;
									2'b01: dwstb <= 4'b0010;
									2'b10: dwstb <= 4'b0100;
									2'b11: dwstb <= 4'b1000;
								endcase
								dwdata <= {4{req_data[7:0]}};
							end
							OP_SH: begin
								case (req_addr[1])
									0: dwstb <= 4'b0011;
									1: dwstb <= 4'b1100;
								endcase
								dwdata <= {2{req_data[15:0]}};
							end
							OP_SW: begin
								dwstb <= 4'b1111;
								dwdata <= req_data;
							end
							default: begin
								dwstb <= 4'b0000;
								dwdata <= 32'b0;
							end
						endcase
					end
				end
				default: begin
					if (dready) begin
						ack_valid <= 1;
						dvalid <= 0;
						dwrite <= 0;
						dwstb <= 0;
						case (req_op)
							OP_LB: begin
								case (daddr[1:0])
									2'b00: ack_data <= $signed(drdata[7:0]);
									2'b01: ack_data <= $signed(drdata[15:8]);
									2'b10: ack_data <= $signed(drdata[23:16]);
									2'b11: ack_data <= $signed(drdata[31:24]);
								endcase
							end
							OP_LBU: begin
								case (daddr[1:0])
									2'b00: ack_data <= drdata[7:0];
									2'b01: ack_data <= drdata[15:8];
									2'b10: ack_data <= drdata[23:16];
									2'b11: ack_data <= drdata[31:24];
								endcase
							end
							OP_LH: begin
								case (daddr[1])
									0: ack_data <= $signed(drdata[15:0]);
									1: ack_data <= $signed(drdata[31:16]);
								endcase
							end
							OP_LHU: begin
								case (daddr[1])
									0: ack_data <= drdata[15:0];
									1: ack_data <= drdata[31:16];
								endcase
							end
							default: begin
								ack_data <= drdata;
							end
						endcase
						mem_state <= STATE_WAIT_REQ;
					end
				end
			endcase
		end
	end
endmodule

module fwrisc_mul_div_shift #(
	parameter ENABLE_MUL_DIV=1,
	parameter ENABLE_MUL=ENABLE_MUL_DIV,
	parameter ENABLE_DIV=ENABLE_MUL_DIV,
	parameter SINGLE_CYCLE_SHIFT=0
	)(
	input clock,
	input reset,
	input[31:0] in_a,
	input[31:0] in_b,
	input[3:0] op,
	input in_valid,
	output reg[31:0] out,
	output reg out_valid);
	parameter [3:0]
		OP_SLL = 4'd0,
		OP_SRL = (OP_SLL + 4'd1),
		OP_SRA = (OP_SRL + 4'd1),
		OP_MUL = (OP_SRA + 4'd1),
		OP_MULH = (OP_MUL + 4'd1),
		OP_MULS = (OP_MULH + 4'd1),
		OP_MULSH = (OP_MULS + 4'd1),
		OP_DIV = (OP_MULSH + 4'd1),
		OP_REM = (OP_DIV + 4'd1),
		OP_NUM_MDS = (OP_REM + 4'd1);
	reg[3:0] op_r;
	reg[4:0] shift_amt_r;
	reg working;
	reg[63:0] mul_res;
	reg[31:0] mul_tmp1;
	reg[31:0] mul_tmp2;
	reg[62:0] div_divisor;
	reg[31:0] div_dividend;
	reg[31:0] div_quotient;
	reg[31:0] div_msk;
	reg div_sign;
	wire mul_tmp2_zero = (|mul_tmp2 == 0);
	always @(posedge clock) begin
		if (reset) begin
			out <= 32'b0;
			out_valid <= 0;
			working <= 0;
			op_r <= 0;
		end else begin
			if (in_valid) begin
				op_r <= op;
				working <= 1;
				case (op)
					OP_SLL, OP_SRL, OP_SRA: begin
						if (SINGLE_CYCLE_SHIFT) begin
							shift_amt_r <= 0;
							case (op_r)
								OP_SLL: begin
									if (|in_b[4:0]) begin
										out <= (out << in_b[4:0]);
									end
								end
								OP_SRL: begin
									if (|in_b[4:0]) begin
										out <= (out >> in_b[4:0]);
									end
								end
								OP_SRA: begin
									if (|in_b[4:0]) begin
										out <= (out >>> in_b[4:0]);
									end
								end
							endcase
						end else begin
							shift_amt_r <= in_b[4:0];
							out <= in_a;
						end
					end
					OP_MUL, OP_MULH: begin
						if (ENABLE_MUL) begin
							mul_res <= 64'b0;
							mul_tmp1 <= in_a;
							mul_tmp2 <= in_b;
							shift_amt_r <= 5'd31;
						end
					end
					OP_MULS, OP_MULSH: begin
						if (ENABLE_MUL) begin
							mul_res <= 64'b0;
							mul_tmp1 <= $signed(in_a);
							mul_tmp2 <= $signed(in_b);
							shift_amt_r <= 5'd31;
						end
					end
					OP_DIV, OP_REM: begin 
						if (ENABLE_DIV) begin
							if (in_a[31]) begin
								div_dividend <= -in_a;
							end else begin
								div_dividend <= in_a;
							end
							if (in_b[31]) begin
								div_divisor <= (-in_b) << 31;
							end else begin
								div_divisor <= in_b << 31;
							end
							shift_amt_r <= 5'd31;
							div_msk <= 'h8000_0000;
						end
					end
				endcase
				if (op == OP_DIV) begin
					div_sign <= (in_a[31] != in_a[31]);
				end else begin
					div_sign <= in_a[31];
				end
			end
			if (working) begin
				case (op_r)
					OP_SLL: begin
						if (|shift_amt_r) begin
							out <= (out << 1);
						end
					end
					OP_SRL: begin
						if (|shift_amt_r) begin
							out <= (out >> 1);
						end
					end
					OP_SRA: begin
						if (|shift_amt_r) begin
							out <= ($signed(out) >>> 1);
						end
					end
					OP_MUL, OP_MULS, OP_MULH, OP_MULSH: begin
						if (ENABLE_MUL) begin
							if (mul_tmp1[0]) begin
								mul_res <= (mul_res + mul_tmp2);
							end
							mul_tmp2 <= (mul_tmp2 << 1);
							mul_tmp1 <= (mul_tmp1 >> 1);
							if (op_r == OP_MUL || op_r == OP_MULS) begin
								out <= mul_res[31:0];
							end else begin // mulh/mulsh
								out <= mul_res[63:32];
							end
						end
					end
					OP_DIV, OP_REM: begin
						if (ENABLE_DIV) begin
							if (div_divisor <= div_dividend) begin
								div_dividend <= div_dividend - div_divisor;
								div_quotient <= div_quotient | div_msk;
							end
							div_divisor <= div_divisor >> 1;
							div_msk <= div_msk >> 1;
							if (op == OP_DIV) begin
								out <= (div_sign)?-div_quotient:div_quotient;
							end else begin
								out <= (div_sign)?-div_dividend:div_dividend;
							end
						end
					end
				endcase
				if (|shift_amt_r == 0) begin
					working <= 1'b0;
					out_valid <= 1'b1;
				end else begin
					shift_amt_r <= shift_amt_r - 1;
				end
			end else begin
				out_valid <= 0;
			end
		end
	end
endmodule

module fwrisc_regfile #(
	parameter ENABLE_COUNTERS=1,
	parameter ENABLE_DEP=1,
	parameter[31:0] VENDORID=0,
	parameter[31:0] ARCHID=0,
	parameter[31:0] IMPID=0,
	parameter[31:0] HARTID=0,
	parameter[31:0] ISA=0
	)(
	input clock,
	input reset,
	output soft_reset_req,
	input instr_complete,
	input trap,
	input tret,
	input irq,
	input[5:0] ra_raddr,
	output reg[31:0] ra_rdata,
	input[5:0] rb_raddr,
	output reg[31:0] rb_rdata,
	input[5:0] rd_waddr,
	input[31:0] rd_wdata,
	input rd_wen,
	output[31:0] dep_lo,
	output[31:0] dep_hi,
	output[31:0] mtvec,
	output reg meie,
	output reg mie);
	parameter [5:0]
		CSR_BASE_Q0 = 6'h20,
		CSR_MVENDORID = (CSR_BASE_Q0 + 1'd1),
		CSR_MARCHID = (CSR_MVENDORID + 1'd1),
		CSR_MIMPID = (CSR_MARCHID + 1'd1),
		CSR_MHARTID = (CSR_MIMPID + 1'd1),
		CSR_BASE_Q1 = 6'h28,
		CSR_MSTATUS = (CSR_BASE_Q1 + 1'd0),
		CSR_MISA = (CSR_MSTATUS + 1'd1),
		CSR_MEDELEG = (CSR_MISA + 1'd1),
		CSR_MIDELEG = (CSR_MEDELEG + 1'd1),
		CSR_MIE = (CSR_MIDELEG + 1'd1),
		CSR_MTVEC = (CSR_MIE + 1'd1),
		CSR_MCOUNTEREN = (CSR_MTVEC + 1'd1),
		CSR_BASE_Q2 = 6'h30,
		CSR_MSCRATCH = (CSR_BASE_Q2 + 1'd0),
		CSR_MEPC = (CSR_MSCRATCH + 1'd1),
		CSR_MCAUSE = (CSR_MEPC + 1'd1),
		CSR_MTVAL = (CSR_MCAUSE + 1'd1),
		CSR_MIP = (CSR_MTVAL + 1'd1),
		CSR_BASE_Q3 = 6'h38,
		CSR_MCYCLE = (CSR_BASE_Q3 + 1'd0),
		CSR_MCYCLEH = (CSR_MCYCLE + 1'd1),
		CSR_MINSTRET = (CSR_MCYCLEH + 1'd1),
		CSR_MINSTRETH = (CSR_MINSTRET + 1'd1),
		CSR_DEP_LO = (CSR_MINSTRETH + 1'd1),
		CSR_DEP_HI = (CSR_DEP_LO + 1'd1),
		CSR_SOFT_RESET = (CSR_DEP_HI + 1'd1);
	reg[63:0] cycle_count;
	reg[63:0] instr_count;
	reg[31:0] dep_lo_r;
	reg[31:0] dep_hi_r;
	reg[31:0] mtvec_r;
	reg[31:0] mscratch;
	reg[31:0] regs['h3f:0];
	generate
	if (ENABLE_DEP) begin
		assign dep_lo = dep_lo_r;
		assign dep_hi = dep_hi_r;
	end else begin
		assign dep_lo = 0;
		assign dep_hi = 0;
	end
	endgenerate
	assign mtvec = mtvec_r;
`ifdef FORMAL
	initial regs[0] = 0;
`else
	`ifdef FWRISC_SOFT_CORE
	initial begin
		$readmemh("regs.hex", regs);
	end
	`endif
`endif
	reg mpie;
	assign soft_reset_req = (rd_wen && rd_waddr == CSR_SOFT_RESET);
	integer reg_i;
	always @(posedge clock) begin
		if (reset) begin
			cycle_count <= 0;
			instr_count <= 0;
			dep_lo_r <= 0;
			dep_hi_r <= 0;
			mtvec_r <= 0;
			mscratch <= {32{1'b0}};
			mtvec_r <= {32{1'b0}};
			meie <= 1'b1;
			mie <= 1'b1;
			mpie <= 1'b0;
			`ifndef FWRISC_SOFT_CORE
			for (reg_i=0; reg_i<'h40; reg_i=reg_i+1) begin
				regs[reg_i] <= {32{1'b0}};
			end
			`endif
		end else begin
			case ({rd_wen, rd_waddr})
				{1'b1, CSR_MCYCLE}: cycle_count <= {cycle_count[63:32], rd_wdata};
				{1'b1, CSR_MCYCLEH}: cycle_count <= {rd_wdata, cycle_count[31:0]};
				default: cycle_count <= cycle_count + 1;
			endcase
			case ({rd_wen, rd_waddr})
				{1'b1, CSR_MINSTRET}: instr_count <= {instr_count[63:32], rd_wdata};
				{1'b1, CSR_MINSTRETH}: instr_count <= {rd_wdata, instr_count[31:0]};
				default: instr_count <= (instr_complete)?(instr_count + 1):instr_count;
			endcase
			if (trap) begin
				mpie <= mie;
				mie <= 1'b0;
			end
			if (tret) begin
				mie <= mpie;
				mpie <= 1'b0;
			end
			if (rd_wen && rd_waddr == CSR_DEP_LO && !dep_lo_r[1]) begin
				dep_lo_r <= rd_wdata;
			end
			if (rd_wen && rd_waddr == CSR_DEP_HI && !dep_hi_r[1]) begin
				dep_hi_r <= rd_wdata;
			end
			if (rd_wen && rd_waddr == CSR_MTVEC) begin
				mtvec_r <= rd_wdata;
			end
			if (rd_wen && rd_waddr == CSR_MIE) begin
				meie <= rd_wdata[11];
			end
			if (rd_wen && rd_waddr == CSR_MSTATUS) begin
				mie <= rd_wdata[3];
				mpie <= rd_wdata[7];
			end
		end
	end
	always @(posedge clock) begin
		if (rd_wen) begin
			if (|rd_waddr) begin
				regs[rd_waddr] <= rd_wdata;
			end else begin
				if (rd_waddr != 0) begin
					$display("Warning: skipping write to %0d", rd_waddr);
				end
			end
		end
		case (ra_raddr) 
			6'b0: ra_rdata <= {32{1'b0}};
			CSR_MVENDORID: ra_rdata <= VENDORID;
			CSR_MARCHID: ra_rdata <= ARCHID;
			CSR_MIMPID: ra_rdata <= IMPID;
			CSR_MHARTID: ra_rdata <= HARTID;
			CSR_MISA: ra_rdata <= {2'b01, ISA[29:0]};
			CSR_MIE: ra_rdata <= {20'b0, meie, 11'b0};
			CSR_MCYCLE: ra_rdata <= cycle_count[31:0];
			CSR_MCYCLEH: ra_rdata <= cycle_count[63:32];
			CSR_MINSTRET: ra_rdata <= instr_count[31:0];
			CSR_MINSTRETH: ra_rdata <= instr_count[63:32];
			CSR_MTVEC: ra_rdata <= mtvec_r;
			CSR_MSCRATCH: ra_rdata <= mscratch;
			CSR_MIP: ra_rdata <= {20'b0, irq, 11'b0};
			default: ra_rdata <= regs[ra_raddr[5:0]];
		endcase
		case (rb_raddr)
			0: rb_rdata <= {32{1'b0}};
			CSR_MVENDORID: rb_rdata <= VENDORID;
			CSR_MARCHID: rb_rdata <= ARCHID;
			CSR_MIMPID: rb_rdata <= IMPID;
			CSR_MHARTID: rb_rdata <= HARTID;
			CSR_MISA: rb_rdata <= {2'b01, ISA[29:0]};
			CSR_MIE: rb_rdata <= {20'b0, meie, 11'b0};
			CSR_MSTATUS: rb_rdata <= {{24{1'b0}}, mpie, {3{1'b0}}, mie, {3{1'b0}}};
			CSR_MCYCLE: rb_rdata <= cycle_count[31:0];
			CSR_MCYCLEH: rb_rdata <= cycle_count[63:32];
			CSR_MINSTRET: rb_rdata <= instr_count[31:0];
			CSR_MINSTRETH: rb_rdata <= instr_count[63:32];
			CSR_MTVEC: rb_rdata <= mtvec_r;
			CSR_MSCRATCH: rb_rdata <= mscratch;
			CSR_MIP: rb_rdata <= {20'b0, irq, 11'b0};
			default: rb_rdata <= regs[rb_raddr[5:0]];
		endcase
	end
endmodule
